mapper.select:

		select d.*, v.recnum, v.viewnum from (

			select * 
			from (select rownum rn,
					 	c.* 
			  			from ( select * from( select * from (
                            				select b.*, a.img 
                            				from (imgboard a right outer join mainboard b on a.bno = b.bno) group by b.bno, b.user_id, b.c_code, b.boardtype, b.title, b.text, b.price, b.m_registdate, b.m_updatedate, a.bno, a.img
                            				) a 
                            inner join categoryboard b on a.c_code = b.c_code
                            where

					mainboard.c_code = #{c_code}
					 bigCategory = #{typeValue}
					 middleCategory = #{typeValue}

					  				)
			  		where

					1 = 1
					text like '%' || #{searchName} || '%' or title like '%'||#{searchName} or user_ID like '%'||#{searchName}||'%'

			  		order by bno desc) c )
		where rn > (#{pageNum} - 1) * #{amount} and rn <= #{pageNum} * #{amount}) d
		inner join v_r_board v on d.bno = v.bno order by d.bno desc


select * 
from (select rownum rn,
			 a.* 
	  from (select *
	  		from (select *
	  				from MAINBOARD inner join categoryboard on mainboard.c_code = categoryboard.c_code
	  				where

			mainboard.c_code = #{c_code}
			 bigCategory = #{typeValue}
			 middleCategory = #{typeValue}

	  				)
	  		where

			1 = 1
			text like '%' || #{searchName} || '%' or title like '%'||#{searchName} or user_ID like '%'||#{searchName}||'%'

	  		order by bno desc) a
	 )
where rn > (#{pageNum} - 1) * #{amount} and rn <= #{pageNum} * #{amount}


select count(*) as total
	from (select * from
	  		(select * from MAINBOARD inner join categoryboard on mainboard.c_code = categoryboard.c_code)
	  		where

			mainboard.c_code = #{c_code}
			bigCategory = #{typeValue}
			middleCategory = #{typeValue}

	  		)
	  where

1 = 1
text like '%' || #{searchName} || '%' or title like '%'||#{searchName} or user_ID like '%'||#{searchName}||'%'
